var re = /^(\d{4}):(\d{2}):(\d{2}) (\d{2}):(\d{2}):(\d{2})$/;

module.exports = function (ed) {
  var p = re.exec(ed);

  if (!p || p.length != 7) return null;

  var yr = parseInt(p[1], 10),
      mo = parseInt(p[2], 10) -1,
      da = parseInt(p[3], 10),
      ho = parseInt(p[4], 10),
      mi = parseInt(p[5], 10),
      se = parseInt(p[6], 10);

  return new Date(Date.UTC(yr, mo, da, ho, mi, se));
};
