var exifdate = require('../');
var test = require('tape');

test('valid dates', function (t) {
  t.plan(3);

  t.equal(
    exifdate('1984:09:21 13:20:55').toISOString(),
    '1984-09-21T13:20:55.000Z'
  );

  t.equal(
    exifdate('2000:02:29 00:00:00').toISOString(),
    '2000-02-29T00:00:00.000Z'
  );

  t.equal(
    exifdate('2000:02:30 00:00:00').toISOString(),
    '2000-03-01T00:00:00.000Z'
  );
});
