var uniqf = require('../');
var test = require('tap').test;
var path = require('path');

var FILE = __dirname + '/files/hasselblad.jpg';
var DEST = __dirname + '/files/2006-03-12_20-43-39.jpg';

test('success', function (t) {
  t.plan(2);

  uniqf(FILE, DEST, function (err, name) {
    t.equal(err, null);
    t.equal(path.basename(name), '2006-03-12_20-43-39.aBeJPTW6mLbUiktsv674Fg.jpg');
  });
});
