var uniqf = require('../');
var test = require('tap').test;
var path = require('path');

var FILE = __dirname + '/files/nonexistent.jpg';

test('nonexistent', function (t) {
  t.plan(2);

  uniqf(FILE, function (err, name) {
    t.similar(err.message, /^ENOENT, open/);
    t.equal(name, undefined);
  });
});
