var uniqf = require('../');
var test = require('tap').test;
var path = require('path');

var FILE = __dirname + '/files/hasselblad.jpg';

test('success', function (t) {
  t.plan(2);

  uniqf(FILE, function (err, name) {
    t.equal(err, null);
    t.equal(path.basename(name), 'hasselblad.aBeJPTW6mLbUiktsv674Fg.jpg');
  });
});
